function getLastDayOfMonth(year,month){return new Date(year,month,0).getDate()}
function getDayOfWeek(year,month,day){const date=new Date(year,month-1,day);return date.getDay()}
let year=parseInt(prompt("Enter Year",2025));let month=parseInt(prompt("Enter month",1));let lastDay=getLastDayOfMonth(year,month);let firstDay=getDayOfWeek(year,month,1);let i=1;let count=1;let emptyCount=0;console.log(firstDay);while(i<=6){let td=``;for(let j=1;j<=7&&count<=lastDay;j++,emptyCount++){if(emptyCount<firstDay){td+=`<td></td>`}else td+=`<td>${count++}</td>`}
let row=` <tr>${td}</tr>`;document.getElementById('calendar').innerHTML+=row;i++}